import { useState } from 'react';

function Cal({ name, math, kor, eng }) {
  const [sum, setSum] = useState();
  const [avg, setAvg] = useState();
  setSum(math + kor + eng);
  setAvg((math + kor + eng) / 3);
  return (
    <div>
      <h1>
        {name}의 총점은 {sum}, 평균은 {avg} 입니다.
      </h1>
    </div>
  );
}
export default Cal;
